apiQuery.query:
query Rewards($customerid: Int!$fromTime: DateTime!$toTime: DateTime!) {
Rewards(customerid: $customerid, fromTime: $fromTime, toTime: $toTime) {
transactionId
customerid
cardNo
cardType
time
amount
reward
merchantName
}

}

query RewardsByWeek($customerid: Int!$limit: Int = 12$offset: Int = 0) {
RewardsByWeek(customerid: $customerid, limit: $limit, offset: $offset) {
customerid
timeWeek
total_reward
}

}

query TotalReward($customerid: Int!) {
TotalReward(customerid: $customerid) {
customerid
total_reward
since_time
}

}

query PotentialRewards($customerid: Int!$cardType: String!$fromTime: DateTime!$toTime: DateTime!) {
PotentialRewards(customerid: $customerid, cardType: $cardType, fromTime: $fromTime, toTime: $toTime) {
transactionId
customerid
rewardCardType
time
amount
reward
merchantName
}

}

query PotentialRewardsByWeek($customerid: Int!$cardType: String!$limit: Int = 12$offset: Int = 0) {
PotentialRewardsByWeek(customerid: $customerid, cardType: $cardType, limit: $limit, offset: $offset) {
customerid
cardType
timeWeek
total_reward
}

}

query TotalPotentialReward($customerid: Int!) {
TotalPotentialReward(customerid: $customerid) {
customerid
cardType
total_reward
since_time
}

}
