apiQuery.query:
query WarrantsByCrime($crime: String$limit: Int = 100$offset: Int = 0) {
WarrantsByCrime(crime: $crime, limit: $limit, offset: $offset) {
crime
num_warrants
}

}

query WarrantsByState($status: String$limit: Int = 100$offset: Int = 0) {
WarrantsByState(status: $status, limit: $limit, offset: $offset) {
state
status
num_warrants
}

}

query BolosByWeekState($state: String$limit: Int = 100$offset: Int = 0) {
BolosByWeekState(state: $state, limit: $limit, offset: $offset) {
week
state
num_bolos
}

}

mutation Tracking($plate: String!$latitude: Float!$longitude: Float!) {
Tracking(encounter: { plate: $plate, latitude: $latitude, longitude: $longitude }) {
_uuid
plate
}

}
