apiQuery.query:
query SensorReading($sensorid: Int!$limit: Int = 10$offset: Int = 0) {
SensorReading(sensorid: $sensorid, limit: $limit, offset: $offset) {
sensorid
temperature
event_time
}

}

query ReadingsAboveTemp($temp: Float!$limit: Int = 10) {
ReadingsAboveTemp(temp: $temp, limit: $limit) {
sensorid
temperature
event_time
}

}

query SensorMaxTempLastMinute($sensorid: Int$limit: Int = 10$offset: Int = 0) {
SensorMaxTempLastMinute(sensorid: $sensorid, limit: $limit, offset: $offset) {
sensorid
maxTemp
last_updated
}

}

query SensorMaxTemp($sensorid: Int$limit: Int = 10$offset: Int = 0) {
SensorMaxTemp(sensorid: $sensorid, limit: $limit, offset: $offset) {
sensorid
maxTemp
last_updated
}

}

mutation AddReading($sensorid: Int!$temperature: Float!) {
AddReading(metric: { sensorid: $sensorid, temperature: $temperature }) {
sensorid
temperature
event_time
}

}

query HighTemps(
    $temperature: Float!
) {
    ReadingsAboveTemp(temp: $temperature) {
        sensorid
        temperature
    }
}

query HighTemps2(
    $temp: Float!
) {
    ReadingsAboveTemp(temp: $temp) {
        sensorid
        temperature
    }
}
